const fetch = ({ids, filter} = {}) => {
    let result = data;

    if (filter) {
        result = result.filter(i => i.Brand.toLowerCase() === filter);
    }

    if (ids) {
        result = result.filter(i => ids.includes(i.id));
    }

    return new Promise((res, rej) => {
        setTimeout(() => res(result), randomInteger(1000, 2000));
    })
};

export const getAll = () => fetch();
export const getByFilter = (filter) => fetch({filter});
export const getByIds = (ids) => fetch({ids});
